"use strict";var btnHalleB=document.getElementById("btnHalleB"),btnHalleG=document.getElementById("btnHalleG"),btnAlleHallen=document.getElementById("btnAlleHallen"),filterElements=document.querySelectorAll(".main-filter-item");btnHalleB.addEventListener("click",function(){filterElements.forEach(n=>{n.classList.contains("halle-g")?n.classList.add("visually-hidden"):n.classList.remove("visually-hidden")})});btnHalleG.addEventListener("click",function(){filterElements.forEach(n=>{n.classList.contains("halle-b")?n.classList.add("visually-hidden"):n.classList.remove("visually-hidden")})});btnAlleHallen.addEventListener("click",function(){filterElements.forEach(n=>{n.classList.remove("visually-hidden")})});